const item = $input.item;

if (!item.json || typeof item.json.content !== 'string' || item.json.content.trim() === '') {
  return [{
    json: {
      foutmelding: 'De AI (Ollama) heeft geen antwoord gegeven.',
      algemeneTekstOfferte: 'Kon de aanvraag niet verwerken omdat de AI niet reageerde.',
      geselecteerdeDiensten: []
    }
  }];
}

const rawOutput = item.json.content;
const match = rawOutput.match(/```json\s*([\s\S]*?)\s*```/);
const cleanedJsonString = match ? match[1].trim() : rawOutput.trim();

try {
  const parsedData = JSON.parse(cleanedJsonString);
  if (!Array.isArray(parsedData.geselecteerdeDiensten)) {
    parsedData.geselecteerdeDiensten = [];
  }
  return [{ json: parsedData }];
} catch (error) {
  return [{
    json: {
      foutmelding: `De AI gaf een ongeldig JSON-antwoord: ${error.message}`,
      algemeneTekstOfferte: 'Kon de aanvraag niet verwerken door een onverwacht AI-antwoord.',
      geselecteerdeDiensten: [],
      rawAiOutput: cleanedJsonString
    }
  }];
}

const inputData = $input.item.json;

const offerteAanvraagData = inputData.offerteAanvraagData;
const geselecteerdeDiensten = inputData.geselecteerdeDiensten;
const volledigePrijslijst = inputData.prijslijstDataArray;

const sanitize = (str) => String(str || '').replace(/[&<>]/g, c => ({'&':'&amp;','<':'&lt;','>':'&gt;'}[c]));

const fouten = [];
let eenmaligeKostenTotaal = 0;
let maandelijkseKostenTotaal = 0;
const verwerkteDiensten = [];

if (!volledigePrijslijst || !geselecteerdeDiensten) {
  fouten.push("Prijslijst of geselecteerde diensten ontbreken.");
  return [{ json: { ...inputData, fouten: fouten } }];
}

for (const aiDienst of geselecteerdeDiensten) {
  const matchingDienst = volledigePrijslijst.find(
    (p) => p.property_number === aiDienst.id
  );

  if (matchingDienst) {
    const aantal = aiDienst.aantal || 1;
    const pageId = matchingDienst.id;
    let eenmaligeKosten = parseFloat(matchingDienst.property_eenmalige_kosten) || 0;
    let maandelijkseKosten = parseFloat(matchingDienst.property_prijs) || 0;

    let prijsPerEenheidTekst = `‚Ç¨${maandelijkseKosten.toFixed(2)}`;
    let totaalTekst = `‚Ç¨${(maandelijkseKosten * aantal).toFixed(2)}`;

    if (matchingDienst.property_eenheid === 'Op aanvraag' || (eenmaligeKosten === 0 && maandelijkseKosten === 0)) {
        prijsPerEenheidTekst = "Op aanvraag";
        totaalTekst = "Op aanvraag";
    } else if (['‚Ç¨/uur', '‚Ç¨/maand'].includes(matchingDienst.property_eenheid)) {
        maandelijkseKostenTotaal += maandelijkseKosten * aantal;
    } else {
        const eenmaligePrijs = eenmaligeKosten > 0 ? eenmaligeKosten : maandelijkseKosten;
        eenmaligeKostenTotaal += eenmaligePrijs * aantal;
        totaalTekst = `‚Ç¨${(eenmaligePrijs * aantal).toFixed(2)}`;
    }

    verwerkteDiensten.push({
      id: pageId,
      dienstNaam: matchingDienst.property_dienst_naam,
      omschrijving: matchingDienst.property_omschrijving,
      keywords: matchingDienst.property_keywords,
      aantal: aantal,
      prijsPerEenheid: prijsPerEenheidTekst,
      totaal: totaalTekst,
      eenheid: matchingDienst.property_eenheid
    });

  } else {
    fouten.push(`Dienst met ID "${aiDienst.id}" niet gevonden in de prijslijst.`);
  }
}

let productTableHtml = `<style>table { width: 100%; border-collapse: collapse; } th, td { border: 1px solid #dddddd; text-align: left; padding: 8px; } th { background-color: #f2f2f2; } tfoot { font-weight: bold; }</style><table><thead><tr><th>Product/Dienst</th><th>Omschrijving</th><th>Aantal</th><th>Prijs p/s</th><th>Totaal</th></tr></thead><tbody>`;
verwerkteDiensten.forEach(dienst => {
  productTableHtml += `<tr><td>${sanitize(dienst.dienstNaam)}</td><td>${sanitize(dienst.omschrijving)}</td><td>${dienst.aantal}</td><td>${dienst.prijsPerEenheid}</td><td>${dienst.totaal}</td></tr>`;
});
productTableHtml += `</tbody><tfoot><tr><td colspan="4" style="text-align: right;">Totaal Eenmalig:</td><td>‚Ç¨ ${eenmaligeKostenTotaal.toFixed(2)}</td></tr><tr><td colspan="4" style="text-align: right;">Totaal Maandelijks:</td><td>‚Ç¨ ${maandelijkseKostenTotaal.toFixed(2)}</td></tr></tfoot></table>`;

return [{
  json: {
    ...offerteAanvraagData,
    offerteDatum: inputData.offerteDatum,
    geselecteerdeDiensten: verwerkteDiensten,
    totaalEenmalig: eenmaligeKostenTotaal.toFixed(2),
    totaalMaandelijks: maandelijkseKostenTotaal.toFixed(2),
    productTableHtml: productTableHtml,
    algemeneTekstOfferte: inputData.algemeneTekstOfferte,
    fouten: fouten
  }
}];

try {
  const item = $input.item.json;

  // STAP 1: DATA VOORBEREIDEN
  const dienstNaam = item.dienstNaam.toLowerCase();
  let aiKeywordsString = item.output || '';

  if (aiKeywordsString.includes('\n')) {
    const lines = aiKeywordsString.split('\n');
    aiKeywordsString = lines[lines.length - 1];
  }

  let aiKeywordsArray = aiKeywordsString.split(',').map(k => k.trim().toLowerCase()).filter(Boolean);

  // STAP 2: VALIDATIE & FILTERING
  if (dienstNaam.includes('mail')) {
    const verbodenWoorden = ['webshop', 'winkel', 'shop', 'e-commerce', 'kassa', 'online store'];
    aiKeywordsArray = aiKeywordsArray.filter(keyword => 
      !verbodenWoorden.some(verbodenWoord => keyword.includes(verbodenWoord))
    );
  }

  if (dienstNaam.includes('shop') || dienstNaam.includes('winkel')) {
    const verbodenWoorden = ['mail', 'mailbox', 'e-mail', 'email'];
    aiKeywordsArray = aiKeywordsArray.filter(keyword => 
      !verbodenWoorden.some(verbodenWoord => keyword.includes(verbodenWoord))
    );
  }

  aiKeywordsArray = aiKeywordsArray.filter(keyword => keyword.length > 2);

  // STAP 3: SAMENVOEGEN & ONTDUBBELEN
  const bestaandeKeywordsArray = (item.keywords || '').split(',').map(k => k.trim()).filter(Boolean);
  const keywordsMap = new Map();
  const teCombinerenArray = [...bestaandeKeywordsArray, ...aiKeywordsArray];

  for (const keyword of teCombinerenArray) {
      keywordsMap.set(keyword.toLowerCase(), keyword);
  }

  const finaleKeywordsArray = [...keywordsMap.values()];
  const finaleKeywordString = finaleKeywordsArray.join(', ');

  // STAP 4: OUTPUT
  item.gecombineerdeKeywords = finaleKeywordString;
  return { json: item };

} catch (error) {
  return { 
    json: { 
      error: `Fout in Code node: ${error.message}`, 
      inputDataDieDeFoutVeroorzaakte: $input.item.json 
    } 
  };
}

